import React, {useState} from 'react';
import './App.css';
import Greeting from './components/hello'

function App() {

  const today = new Date()
  const [timer, setTime] = useState(today.toLocaleTimeString());

  setInterval(()=>{ setTime(today.toLocaleTimeString())}, 1000)

  return (
    <div className="App">
      <Greeting time={timer} />
    </div>
  );
}

export default App;
