import React, {Fragment, useEffect, useState} from 'react';

const Greeting = (props) => {

    let [userName, setUserName] = useState('cargando nombre')

    let obtainData = async (id) => {
        let crude = await fetch('https://jsonplaceholder.typicode.com/users');
        let response = await crude.json();
        let finalData = await response[id]
        return await finalData
    }

    let obtainName = async (id) => {
        let dataHandler = await obtainData(id)
        let name = await dataHandler.name
        return await name
    }

    useEffect(async () => {
        setUserName(await obtainName(0))
    })

    return(
        <Fragment>
            <h1> Bienvenido {userName}!</h1>
        </Fragment>
    )
}

export default Greeting;
