import React from "react";


const ThemeChange = (props) => {
   
    let body = document.querySelector('body');

    body.classList.add('light')

    const change = () => {
        if (body.className === 'light'){
            body.className = 'dark'
            window.localStorage.getItem('theme') === null ? localStorage['theme'] = 'dark': window.localStorage.removeItem('theme');
        }else{
            body.className = 'light'
            window.localStorage.getItem('theme') === null ? localStorage['theme'] = 'light': window.localStorage.removeItem('theme');
        }
    }

    return(
        <button onClick={change} className="btn"> 
            <p>Cambiar Tema</p>
            <div
                id="theme-logo"
                alt="Change Theme Button"
            ></div>
        </button>
    ) 
}

export default ThemeChange
