import React, {useEffect} from "react";

const ThemeChange = () => {
   
    let body = document.querySelector('body');

    const change = () => {
        body.classList.toggle('light', 'dark')
    }

    useEffect(() => {
        document.querySelector('#theneme_changer').addEventListener('click', () => {
            change();
        })
    })
    return(
        <button id="theme_changer"></button>
    ) 
}

export default ThemeChange
