import React, {Fragment, useState} from 'react';

const Greeting = (props) => {

    let actualDate = new Date();

    const [hora, Sethora] = useState(actualDate.toLocaleTimeString())

    return(
        <Fragment>
            <h1> Hola Forastero! {() => {setInterval(() => {Sethora(actualDate.toLocaleTimeString());}, 2000); return hora}} </h1>
        </Fragment>
    )
}

export default Greeting;
