import React, {Fragment, useState} from 'react';

const Greeting = (props) => {

    const [hora, setHora] = useState( props.date.toLocaleTimeString())

    setInterval(() => { setHora(props.date.toLocaleTimeString())}, 1000)

    return(
        <Fragment>
            <h1> Hola Forastero!</h1>
            <h2> Ahora mismo son las {setHora} </h2>
        </Fragment>
    )
}

export default Greeting;
