import React, {useContext} from "react";

const themeChange = ()=>{
   
    let body = document.getElementsByTagName('body');

    const change = () => {
        body.classList.toggle('light', 'dark')
    }

    return(
        <button onClick={change}></button>
    ) 
}

export default themeChange
