import React, {Fragment, useState} from 'react';

const Greeting = (props) => {

    const hoy = new Date;

    const [hora, sethora] = useState(hoy.toLocaleTimeString())

    setInterval(() => {sethora(hoy.toLocaleTimeString())}, 1000)

    return(
        <Fragment>
            <h1> Hola Forastero! {hora} </h1>
        </Fragment>
    )
}

export default Greeting;
