import React from "react";

const ThemeChange = () => {
   
    let body = document.querySelector('body');

    body.classList.add('light')

    const change = () => {
        body.className = 'light' ?  body.className = 'dark' : body.className = 'light';
    }

    return(
        <button onClick={change()}></button>
    ) 
}

export default ThemeChange
