import React from "react";

const themeChange = ()=>{
   
    let body = document.querySelector('body');

    const change = () => {
        body.classList.toggle('light', 'dark')
    }

    document.querySelector('#theme_changer').addEventListener('click', change())

    return(
        <button id="theme_changer"></button>
    ) 
}

export default themeChange
