import React, {Fragment, useEffect, useState} from 'react';

const Greeting = (props) => {

    let [userData, setUserData] = useState("Cargando sus datos, espere porfavor")
    let [userName, setUserName] = useState('cargando datos')

    let obtainData = async (id) => {
        let crude = await fetch('https://jsonplaceholder.typicode.com/users');
        let response = await crude.json();
        let finalData = await response[id]
        return await finalData
    }

    useEffect(() => {
        async function fetchData(){
            let userFinalData = await obtainData(0);
            setUserData(await userFinalData)
            setUserName(await userFinalData.name)
        }
        fetchData()
    }, [])

    return(
        <Fragment>
            <h1> Bienvenido {userName}!</h1>
            <p>
                {JSON.stringify(userData)}
            </p>
        </Fragment>
    )
}

export default Greeting;
